mapper.update:
UPDATE dish
name = #{name}, category_id = #{categoryId}, price = #{price}, image = #{image}, description = #{description}, status = #{status}, updateTime = #{updateTime}, updateUser = #{updateUser},
WHERE id = #{id}

mapper.select:
select  * from  dish


        and name like concat('%',#{name},'%')

    and category_id=#{categoryId}
    and status=#{status} 

order by  id desc
